<?php/** * File AdminBaseController.php * * PHP version 5.4+ * * @author    Dzung Nguyen * @copyright 2010-2014 evolpas * @license   http://www.evolpas.com/license license * @version   XXX * @link      http://www.evolpas.com * @category  controllers * @package   Controllers\Admin *//** * Class AdminBaseController * * Defined a base controller for admin's controllers. * * @author    Dzung Nguyen * @copyright 2013-2014 evolpas * @license   http://www.evolpas.com/license license * @version   XXX * @link      http://www.evolpas.com * @category  controllers * @package   Controllers\Admin * @since     XXX */namespace Admin;class AdminBaseController extends \BaseController {    protected $layout = 'layouts.admin';    public function __construct() {        $defaultLoacle = \DB::table('app_languages')                ->where('is_default', 1)                ->first(array('code'));        if ($defaultLoacle !== null) {            \App::setLocale($defaultLoacle->code);        }        $this->beforeFilter('csrf', array('on' => array('store', 'update', 'delete', 'put')));    }}