<?php/** * File AdminResource.php * PHP version 5.4+ * @author    Dzung Nguyen * @copyright 2010-2014 evolpas * @license   http://www.evolpas.com/license license * @version   XXX * @link      http://www.evolpas.com * @category  Model * @package   Model\AdminGroup *//** * Class AdminResource * * This is a model to defined all routes of actions in application.  * For simple, it's only defined as an array. * It should be extanded by organize to store data table.  * * @author    Dzung Nguyen * @copyright 2013-2014 evolpas * @license   http://www.evolpas.com/license license * @version   XXX * @link      http://www.evolpas.com * @category  Model * @package   Model\AdminGroup * @since     XXX */class AdminResource {	/**	 * List to defined all actions of application based on routes of them	 */	public static $resources = array(		'manage_users' => array(			'list_user' => ['admin.users.index'],			'create_user' => ['admin.users.create', 'admin.users.store'],			'edit_user' => ['admin.users.edit', 'admin.users.update'],			'delete_user' => ['admin.users.destroy']		),		'manage_groups' => array(			'list_group' => ['admin.groups.index'],			'create_group' => ['admin.groups.create', 'admin.users.store'],			'edit_group' => ['admin.groups.edit', 'admin.groups.store'],			'delete_group' => ['admin.groups.destroy'],			'set_permission' => ['admin.groups.permission']		),		'settings' => array(			'languages' => array('admin.language.translation', 'admin.language.index', 'admin.language.add')		),	);	/**	 * Get array route that be defined	 */	public static function all() {		$routes = array();		foreach (self::$resources as $k => $resource) {			foreach ($resource as $k => $v) {				$routes = array_merge($routes, $v);			}		}		return $routes;	}}