<?phpRoute::group(array('namespace' => 'Admin', 'prefix' => 'admin'), function() {    Route::get('/login', array(        'as' => 'admin.login',        'uses' => 'HomeController@getLogin'    ));    Route::post('/login', array(        'as' => 'admin.login',        'uses' => 'HomeController@postLogin'    ));    Route::get('error/{type}', array(        'as' => 'admin.error',        'uses' => 'HomeController@error'    ));});Route::group(array('namespace' => 'Admin', 'prefix' => 'admin', 'before' => 'admin.auth'), function() {    Route::get('/', array('as' => 'admin.root', 'uses' => 'HomeController@index'));    Route::get('/logout', array(        'as' => 'admin.logout',        'uses' => 'HomeController@logout',    ));    Route::get('/users/profile', array(        'as' => 'admin.users.profile',        'uses' => 'AdminUserController@profile'    ));    Route::post('users/profile', array(        'as' => 'admin.users.profile',        'uses' => 'AdminUserController@postProfile'    ));    Route::get('users/password', array(        'as' => 'admin.users.password',        'uses' => 'AdminUserController@password'    ));    Route::post('users/password', array(        'as' => 'admin.users.password',        'uses' => 'AdminUserController@postPassword'    ));    Route::get('datatables/users', 'AdminUserController@datatables');    Route::group(array('before' => 'admin.permission'), function() {        Route::resource('users', 'AdminUserController');        Route::resource('groups', 'AdminGroupController');        Route::get('group/{id}/permission', array(            'as' => 'admin.groups.permission',            'uses' => 'AdminGroupController@getPermission'        ));        Route::post('group/{id}/permission', array(            'as' => 'admin.groups.permission',            'uses' => 'AdminGroupController@postPermission',        ));        Route::get('language/translation', array(            'as' => 'admin.language.translation',            'uses' => 'LanguageController@getTranslation'        ));        Route::post('language/translation', array(            'as' => 'admin.language.translation',            'uses' => 'LanguageController@postTranslation'        ));        Route::get('languages', array(            'as' => 'admin.language.index',            'uses' => 'LanguageController@index'        ));        Route::get('language/default/{id}', array(            'as' => 'admin.language.default',            'uses' => 'LanguageController@setDefault'        ));        Route::post('language/add', array(            'as' => 'admin.language.add',            'uses' => 'LanguageController@add'        ));    });});